document.addEventListener('DOMContentLoaded', function () {
    var swiper = new Swiper('.swiper-container', {
      loop: true, 
      autoplay: {
        delay: 5000, //ミリ秒
        disableOnInteraction: false,  // ユーザーがインタラクションしても自動スライドを止めない
      },
   slidesPerView: 1,  // 1枚ずつ表示
    effect: 'slide',  // スライドエフェクト
    speed: 1000,  //ミリ秒
    easing: 'ease-out',  // イージング効果
    pagination: {
      el: '.swiper-pagination',  // ページネーション（丸いボタン）の設定
      clickable: true,  // ドットクリックで移動可能
      type: 'bullets',  // ドット形式
      dynamicBullets: true,  // ドット数を動的に変更
    },
      navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
      },
    });
  });